/* global console */// GLOBAL FANCYBOX CONFIG VARIABLES
var FB_PADDING=0,FB_TOP_RATIO=.25,FB_CLOSE_CLICK=!1,FB_OL_BG="rgba(0,0,0,0.9)";$(document).ready(function(){$("header nav ul li a").onMenuSelect();$(".showcase-container .grid").onGridHover();$(".showcase-container .grid").onGridClick();$(".dialog-container").dialog({modal:!0,autoOpen:!1,buttons:{Ok:function(){$(this).dialog("close")}}})});$.fn.onMenuSelect=function(){this.on("click",function(){$(this.parentNode.parentNode.childNodes).each(function(){$(this).removeClass("active")});$(this.parentNode).addClass("active")})};$.fn.onGridHover=function(){this.hover(function(){$(this).find(".overlay").hide().fadeIn(500);$(this).toggleClass("active").next().stop(!0,!0)})};$.fn.onGridClick=function(){this.click(function(){var e=$(this).find("img").attr("alt"),t=$(this).find("img").attr("data-subtitle"),n=$(this).find("img").attr("data-src");n.length?$(this).hasClass("video")?$(this).getVideo(e,t,n):$(this).getVideo(e,t,n):$(this).showMessage("No Source Found!","No source was found or specified for this grid.")})};$.fn.getVideo=function(e,t,n){var r=640,i=360;n.indexOf("mediastreamer")>=0&&(i=410);$.fancybox({href:n,title:e,padding:FB_PADDING,topRatio:FB_TOP_RATIO,width:r,height:i,type:"iframe",helpers:{title:null,overlay:{closeClick:FB_CLOSE_CLICK,css:{background:FB_OL_BG}},media:!0},afterLoad:function(){this.inner.before("<h2>"+this.title+"</h2><h3>"+t+"</h3>")}})};$.fn.showMessage=function(e,t){$(".dialog-container").dialog({title:e});$(".dialog-container").dialog("open");$(".dialog-container").html(t)};