/* global console */// GLOBAL FANCYBOX CONFIG VARIABLES
var FB_PADDING=0,FB_TOP_RATIO=.25;$(document).ready(function(){$("header nav ul li a").onMenuSelect();$(".showcase-container .grid").onGridHover();$(".showcase-container .grid").onGridClick()});$.fn.onMenuSelect=function(){this.on("click",function(){$(this.parentNode.parentNode.childNodes).each(function(){$(this).removeClass("active")});$(this.parentNode).addClass("active")})};$.fn.onGridHover=function(){this.hover(function(){$(this).find(".overlay").hide().fadeIn(500);$(this).toggleClass("active").next().stop(!0,!0)})};$.fn.onGridClick=function(){this.click(function(){var e=$(this).find("img").attr("alt"),t=$(this).find("img").attr("data-subtitle"),n=$(this).find("img").attr("data-src");console.log(t);$(this).hasClass("video")?$(this).getVideo(e,t,n):$(this).getVideo(e,t,n)})};$.fn.getVideo=function(e,t,n){var r=640,i=360;n.indexOf("mediastreamer")>=0&&(i=410);$.fancybox({href:n,title:e,padding:FB_PADDING,topRatio:FB_TOP_RATIO,width:r,height:i,type:"iframe",helpers:{title:null,overlay:{closeClick:!1,css:{background:"rgba(0,0,0, 0.9)"}},media:!0},afterLoad:function(){this.inner.before("<h2>"+this.title+"</h2><h3>"+t+"</h3>")}})};